function [x1,n,msg] = fNewtonRaphson(f,x0, x1, e = 0.000001, nmax = 100)    n = 0; %numero de iteraciones    msg = "Error";    if e <= 0      msg = "error < 0"      return;    endif    if nmax < 2 || nmax > 1000      msg = "nmax exagerado";      return;    endif    error = e+1;        while error > e && n <= nmax      x1 = x0 - f(x0)/f1(x0);      error = abs(x1 - x0);      error /= x1;      x0 = x1;      n++;    endwhile    % Mensaje de salida    if n >= nmax      msg = "Límite de iteraciones excedido. No converge";      return;    endif      msg = "Ejecución válida. Converge";   endfunction